import React, { useState } from "react";

const App = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    numberOfPeople: "",
    eventType: "",
    theme: "",
    extraRequirements: "",
    venue: "",
    eventDate: "",
    eventTime: "",
  });

  const [submitted, setSubmitted] = useState(false);

  const handleChange = (e) => {
    setFormData((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // TODO: Connect to backend API to submit formData
    console.log("Form submitted:", formData);
    setSubmitted(true);
  };

  return (
    <>
      <style>{`
        @import url('https://fonts.googleapis.com/css2?family=Poppins:wght@300;600&display=swap');

        * {
          box-sizing: border-box;
        }
        body, html, #root {
          margin: 0; padding: 0; height: 100%;
          font-family: 'Poppins', sans-serif;
          background: linear-gradient(135deg, #0f2027, #203a43, #2c5364);
          overflow: hidden;
          color: #fff;
        }
        .background {
          position: fixed;
          top: 0; left: 0; width: 100%; height: 100%;
          background: radial-gradient(circle at center, #1f2937, #0f2027 70%);
          overflow: hidden;
          z-index: -1;
        }
        /* Animated floating circles */
        .circle {
          position: absolute;
          border-radius: 50%;
          background: rgba(29, 185, 84, 0.15);
          animation-timing-function: ease-in-out;
          animation-iteration-count: infinite;
          animation-direction: alternate;
          filter: blur(10px);
        }
        .circle1 {
          width: 180px; height: 180px;
          top: 10%;
          left: 15%;
          animation-name: float1;
          animation-duration: 7s;
        }
        .circle2 {
          width: 120px; height: 120px;
          top: 40%;
          left: 75%;
          animation-name: float2;
          animation-duration: 10s;
        }
        .circle3 {
          width: 200px; height: 200px;
          top: 70%;
          left: 35%;
          animation-name: float3;
          animation-duration: 12s;
        }
        @keyframes float1 {
          0% { transform: translateY(0) translateX(0); }
          100% { transform: translateY(20px) translateX(15px); }
        }
        @keyframes float2 {
          0% { transform: translateY(0) translateX(0); }
          100% { transform: translateY(-15px) translateX(-10px); }
        }
        @keyframes float3 {
          0% { transform: translateY(0) translateX(0); }
          100% { transform: translateY(25px) translateX(-20px); }
        }

        .container {
          display: flex;
          justify-content: center;
          align-items: center;
          min-height: 100vh;
          padding: 1rem;
        }
        .form-box {
          background: rgba(0,0,0,0.75);
          padding: 2rem 2.5rem;
          border-radius: 12px;
          max-width: 480px;
          width: 100%;
          box-shadow: 0 0 15px rgba(29, 185, 84, 0.6);
          transition: box-shadow 0.3s ease;
        }
        .form-box:hover {
          box-shadow: 0 0 25px rgba(29, 185, 84, 0.9);
        }
        h2 {
          margin-bottom: 1.5rem;
          font-weight: 600;
          text-align: center;
          color: #1DB954;
          text-transform: uppercase;
          letter-spacing: 2px;
        }
        form {
          display: flex;
          flex-direction: column;
        }
        label {
          font-weight: 600;
          margin-bottom: 0.4rem;
          font-size: 0.9rem;
        }
        input, select, textarea {
          padding: 10px 12px;
          border-radius: 8px;
          border: none;
          font-size: 1rem;
          margin-bottom: 1rem;
          outline: none;
          transition: box-shadow 0.3s ease;
        }
        input:focus, select:focus, textarea:focus {
          box-shadow: 0 0 10px #1DB954;
        }
        textarea {
          resize: vertical;
          min-height: 70px;
        }
        button {
          background-color: #1DB954;
          color: white;
          font-weight: 700;
          padding: 12px;
          border: none;
          border-radius: 10px;
          cursor: pointer;
          font-size: 1.1rem;
          transition: background-color 0.3s ease;
        }
        button:hover {
          background-color: #14a343;
        }
        .thank-you {
          text-align: center;
          font-size: 1.3rem;
          color: #1DB954;
        }

        /* Responsive */
        @media (max-width: 520px) {
          .form-box {
            padding: 1.5rem 1.8rem;
          }
        }
      `}</style>

      <div className="background">
        <div className="circle circle1"></div>
        <div className="circle circle2"></div>
        <div className="circle circle3"></div>
      </div>

      <div className="container">
        <div className="form-box">
          <h2>Event Management Details</h2>

          {submitted ? (
            <div className="thank-you">
              <p>🎉 Thank you for submitting your event details! We will contact you shortly.</p>
            </div>
          ) : (
            <form onSubmit={handleSubmit}>
              <label htmlFor="name">Full Name *</label>
              <input
                type="text"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleChange}
                required
                placeholder="Enter your full name"
              />

              <label htmlFor="email">Email Address *</label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
                placeholder="example@mail.com"
              />

              <label htmlFor="phone">Phone Number *</label>
              <input
                type="tel"
                id="phone"
                name="phone"
                value={formData.phone}
                onChange={handleChange}
                required
                placeholder="+1 234 567 8900"
              />

              <label htmlFor="numberOfPeople">Number of People *</label>
              <input
                type="number"
                id="numberOfPeople"
                name="numberOfPeople"
                min="1"
                value={formData.numberOfPeople}
                onChange={handleChange}
                required
                placeholder="Total expected guests"
              />

              <label htmlFor="eventType">Event Type *</label>
              <select
                id="eventType"
                name="eventType"
                value={formData.eventType}
                onChange={handleChange}
                required
              >
                <option value="">Select event type</option>
                <option value="wedding">Wedding</option>
                <option value="birthday">Birthday</option>
                <option value="corporate">Corporate Event</option>
                <option value="concert">Concert</option>
                <option value="conference">Conference</option>
                <option value="other">Other</option>
              </select>

              <label htmlFor="theme">Theme / Style *</label>
              <input
                type="text"
                id="theme"
                name="theme"
                value={formData.theme}
                onChange={handleChange}
                required
                placeholder="E.g., Rustic, Modern, Vintage..."
              />

              <label htmlFor="extraRequirements">Extra Requirements</label>
              <textarea
                id="extraRequirements"
                name="extraRequirements"
                value={formData.extraRequirements}
                onChange={handleChange}
                placeholder="Anything else we should know?"
              />

              <label htmlFor="venue">Venue *</label>
              <input
                type="text"
                id="venue"
                name="venue"
                value={formData.venue}
                onChange={handleChange}
                required
                placeholder="Event location"
              />

              <label htmlFor="eventDate">Event Date *</label>
              <input
                type="date"
                id="eventDate"
                name="eventDate"
                value={formData.eventDate}
                onChange={handleChange}
                required
              />

              <label htmlFor="eventTime">Event Time *</label>
              <input
                type="time"
                id="eventTime"
                name="eventTime"
                value={formData.eventTime}
                onChange={handleChange}
                required
              />

              <button type="submit">Submit Event Details</button>
            </form>
          )}
        </div>
      </div>
    </>
  );
};

export default App;
