import React, { useState, useEffect, useRef } from "react";

const App = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    age: "",
    eventType: "",
    peopleCount: "",
    theme: "",
    extraRequirements: "",
    venue: "",
    date: "",
    time: "",
  });

  // Scroll state and speed tracking
  const [scrollY, setScrollY] = useState(0);
  const [scrollSpeed, setScrollSpeed] = useState(0);
  const lastScrollY = useRef(0);
  const lastTimestamp = useRef(Date.now());

  useEffect(() => {
    const handleScroll = () => {
      const currentY = window.scrollY;
      const now = Date.now();
      const deltaY = Math.abs(currentY - lastScrollY.current);
      const deltaTime = now - lastTimestamp.current;

      const speed = deltaTime > 0 ? deltaY / deltaTime : 0;

      setScrollY(currentY);
      setScrollSpeed(speed);
      lastScrollY.current = currentY;
      lastTimestamp.current = now;
    };

    window.addEventListener("scroll", handleScroll, { passive: true });
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const maxScroll = 200;
  const scrollPercent = Math.min(scrollY / maxScroll, 1);
  const hue = 140 + scrollPercent * 40;
  const confettiSpeedMultiplier = Math.min(1 + scrollSpeed * 100, 3);

  const rootStyle = {
    "--green-hue": hue,
    "--confetti-speed": confettiSpeedMultiplier,
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert("Form submitted! Check console for data.");
    console.log("Form Data:", formData);
  };

  return (
    <>
      <style>{`
        @import url('https://fonts.googleapis.com/css2?family=Poppins:wght@300;500&display=swap');

        body, html, #root {
          margin: 0; padding: 0; height: 100%;
          font-family: 'Poppins', sans-serif;
          overflow-y: auto;
          background: radial-gradient(circle at center, hsl(var(--green-hue), 70%, 15%) 0%, #0f1012 80%);
          color: #fff;
          transition: background 0.3s ease;
          position: relative;
        }

        .container {
          min-height: 100vh;
          display: flex;
          justify-content: center;
          align-items: center;
          padding: 2rem 1rem;
          position: relative;
          z-index: 10;
        }

        .form-box {
          background: rgba(0, 0, 0, 0.45);
          padding: 2rem 3rem;
          border-radius: 20px;
          max-width: 520px;
          width: 100%;
          box-shadow: 0 0 30px hsl(var(--green-hue), 80%, 50%);
          backdrop-filter: blur(12px);
          -webkit-backdrop-filter: blur(12px);
          overflow: auto;
          max-height: 90vh;
          color: #fff;
          border: 2px solid hsla(var(--green-hue), 90%, 60%, 0.6);
          transition: border-color 0.3s ease, box-shadow 0.3s ease;
          position: relative;
          z-index: 20;
        }

        form {
          display: flex;
          flex-direction: column;
        }

        label {
          margin-bottom: 0.3rem;
          font-weight: 500;
          margin-top: 1rem;
        }

        input, select, textarea {
          padding: 0.7rem 1rem;
          border-radius: 12px;
          border: none;
          outline: none;
          font-size: 1rem;
          background: rgba(255 255 255 / 0.1);
          color: #fff;
          transition: background-color 0.3s ease;
        }
        input::placeholder, textarea::placeholder {
          color: #ddd;
        }
        input:focus, select:focus, textarea:focus {
          background: rgba(255 255 255 / 0.25);
          box-shadow: 0 0 8px hsl(var(--green-hue), 90%, 70%);
        }

        textarea {
          resize: vertical;
          min-height: 80px;
        }

        button {
          margin-top: 2rem;
          padding: 0.8rem 0;
          border-radius: 30px;
          border: none;
          background: hsl(var(--green-hue), 90%, 60%);
          box-shadow: 0 0 15px hsl(var(--green-hue), 90%, 60%);
          color: #fff;
          font-weight: 600;
          font-size: 1.2rem;
          cursor: pointer;
          transition: background-color 0.25s ease, box-shadow 0.25s ease;
        }
        button:hover {
          background: hsl(calc(var(--green-hue) - 20), 90%, 50%);
          box-shadow: 0 0 20px hsl(calc(var(--green-hue) - 20), 90%, 50%);
        }

        /* Confetti animation */
        @keyframes confettiFall {
          0% {
            transform: translateY(-20px) rotate(0deg);
            opacity: 1;
          }
          100% {
            transform: translateY(110vh) rotate(360deg);
            opacity: 0;
          }
        }
        .confetti {
          animation-timing-function: linear;
          animation-iteration-count: infinite;
          animation-name: confettiFall;
          animation-duration: calc(6s / var(--confetti-speed));
          position: absolute;
          bottom: 100vh;
          z-index: 0;
          opacity: 0.8;
        }
      `}</style>

      {/* Confetti pieces */}
      {[...Array(30)].map((_, i) => {
        const left = Math.random() * 100 + "%";
        const delay = Math.random() * 10 + "s";
        const size = Math.random() * 12 + 6 + "px";
        const colors = ["#ff0a54", "#ff477e", "#ff85a1", "#fbb1b1", "#f9bec7"];
        return (
          <div
            key={i}
            className="confetti"
            style={{
              left,
              width: size,
              height: size,
              backgroundColor: colors[i % colors.length],
              borderRadius: Math.random() > 0.5 ? "50%" : "20%",
              animationDelay: delay,
            }}
          />
        );
      })}

      <div className="container" role="main" style={rootStyle}>
        <div className="form-box" role="form" aria-label="Event Management Form">
          <h2 style={{ textAlign: "center" }}>Event Management Service</h2>

          <form onSubmit={handleSubmit}>

            <label htmlFor="name">Name:</label>
            <input
              type="text"
              id="name"
              name="name"
              placeholder="Your full name"
              value={formData.name}
              onChange={handleChange}
              required
            />

            <label htmlFor="email">Email Address:</label>
            <input
              type="email"
              id="email"
              name="email"
              placeholder="example@mail.com"
              value={formData.email}
              onChange={handleChange}
              required
            />

            <label htmlFor="age">Age:</label>
            <input
              type="number"
              id="age"
              name="age"
              placeholder="Your age"
              value={formData.age}
              onChange={handleChange}
              min="0"
              max="150"
              required
            />

            <label htmlFor="eventType">Type of Event:</label>
            <select
              id="eventType"
              name="eventType"
              value={formData.eventType}
              onChange={handleChange}
              required
            >
              <option value="">-- Select Event Type --</option>
              <option value="wedding">Wedding</option>
              <option value="birthday">Birthday</option>
              <option value="conference">Conference</option>
              <option value="party">Party</option>
              <option value="concert">Concert</option>
              <option value="other">Other</option>
            </select>

            <label htmlFor="peopleCount">Number of People Attending:</label>
            <input
              type="number"
              id="peopleCount"
              name="peopleCount"
              placeholder="Estimated number of attendees"
              value={formData.peopleCount}
              onChange={handleChange}
              min="1"
              required
            />

            <label htmlFor="theme">Event Theme:</label>
            <input
              type="text"
              id="theme"
              name="theme"
              placeholder="e.g. Retro, Elegant, Casual"
              value={formData.theme}
              onChange={handleChange}
              required
            />

            <label htmlFor="extraRequirements">Extra Requirements:</label>
            <textarea
              id="extraRequirements"
              name="extraRequirements"
              placeholder="Any special requests or notes"
              value={formData.extraRequirements}
              onChange={handleChange}
            />

            <label htmlFor="venue">Venue:</label>
            <input
              type="text"
              id="venue"
              name="venue"
              placeholder="Venue address or location"
              value={formData.venue}
              onChange={handleChange}
              required
            />

            <label htmlFor="date">Date of Event:</label>
            <input
              type="date"
              id="date"
              name="date"
              value={formData.date}
              onChange={handleChange}
              required
            />

            <label htmlFor="time">Time of Event:</label>
            <input
              type="time"
              id="time"
              name="time"
              value={formData.time}
              onChange={handleChange}
              required
            />

            <button type="submit">Submit Request</button>
          </form>
        </div>
      </div>
    </>
  );
};

export default App;
