        button:hover {
          background-color: #14a343;
          box-shadow: 0 6px 16px rgba(29, 185, 84, 0.9);
        }
        .thank-you {
          text-align: center;
          font-size: 1.3rem;
          color: #1db954;
          text-shadow: 0 0 10px #1db954;
        }

        /* Responsive */
        @media (max-width: 600px) {
          .form-box {
            padding: 1.5rem 2rem;
            max-width: 100%;
          }
        }
      `}</style>

      <div className="background">
        <div className="glow-circle glow1"></div>
        <div className="glow-circle glow2"></div>
        <div className="glow-circle glow3"></div>

        {/* Confetti elements */}
        {[...Array(25)].map((_, i) => (
          <div
            key={i}
            className="confetti"
            style={{
              left: `${Math.random() * 100}%`,
              animationDuration: `${3 + Math.random() * 5}s`,
              animationDelay: `${Math.random() * 5}s`,
              backgroundColor: `hsl(${Math.floor(Math.random() * 360)}, 70%, 80%)`,
              width: `${6 + Math.random() * 6}px`,
              height: `${6 + Math.random() * 6}px`,
              borderRadius: Math.random() > 0.5 ? "50%" : "0",
            }}
          />
        ))}
      </div>

      <div className="container">
        <div className="form-box" role="form">
          <h2>Event Management Details</h2>
          {submitted ? (
            <p className="thank-you">Thanks for submitting your event details! We will get back to you soon.</p>
          ) : (
            <form onSubmit={handleSubmit} noValidate>
              <label htmlFor="name">Full Name</label>
              <input
                type="text"
                id="name"
                name="name"
                placeholder="Enter your full name"
                value={formData.name}
                onChange={handleChange}
                required
              />

              <label htmlFor="email">Email Address</label>
              <input
                type="email"
                id="email"
                name="email"
                placeholder="example@mail.com"
                value={formData.email}
                onChange={handleChange}
                required
              />

              <label htmlFor="phone">Phone Number</label>
              <input
                type="tel"
                id="phone"
                name="phone"
                placeholder="+91 1234567890"
                value={formData.phone}
                onChange={handleChange}
                required
              />

              <label htmlFor="numberOfPeople">Number of People Attending</label>
              <input
                type="number"
                id="numberOfPeople"
                name="numberOfPeople"
                placeholder="Enter number of attendees"
                value={formData.numberOfPeople}
                onChange={handleChange}
                min="1"
                required
              />

              <label htmlFor="eventType">Type of Event</label>
              <select
                id="eventType"
                name="eventType"
                value={formData.eventType}
                onChange={handleChange}
                required
              >
                <option value="">-- Select event type --</option>
                <option value="birthday">Birthday Party</option>
                <option value="wedding">Wedding</option>
                <option value="corporate">Corporate Event</option>
                <option value="concert">Concert</option>
                <option value="other">Other</option>
              </select>

              <label htmlFor="theme">Theme (if any)</label>
              <input
                type="text"
                id="theme"
                name="theme"
                placeholder="E.g., Retro, Beach, Formal"
                value={formData.theme}
                onChange={handleChange}
              />

              <label htmlFor="extraRequirements">Extra Requirements</label>
              <textarea
                id="extraRequirements"
                name="extraRequirements"
                placeholder="Anything else you need?"
                value={formData.extraRequirements}
                onChange={handleChange}
              />

              <label htmlFor="venue">Venue</label>
              <input
                type="text"
                id="venue"
                name="venue"
                placeholder="Enter venue address"
                value={formData.venue}
                onChange={handleChange}
                required
              />

              <label htmlFor="eventDate">Date of Event</label>
              <input
                type="date"
                id="eventDate"
                name="eventDate"
                value={formData.eventDate}
                onChange={handleChange}
                required
              />

              <label htmlFor="eventTime">Time of Event</label>
              <input
                type="time"
                id="eventTime"
                name="eventTime"
                value={formData.eventTime}
                onChange={handleChange}
                required
              />

              <button type="submit">Submit Details</button>
            </form>
          )}
        </div>
      </div>
    </>
  );
};

export default App;
